stream = ""


def get_marker_index(marker_length):
    for index, _ in enumerate(stream):
        characters = stream[index : index + marker_length]
        if len(set(list(characters))) == marker_length:
            return index + marker_length


print("Part 1 marker location: " + str(get_marker_index(4)))
print("Part 2 marker location: " + str(get_marker_index(14)))
